"use strict";

var intro = document.querySelector(".intro"), subtitle = document.querySelector(".intro__subtitle");

function changeColors() {
    intro.classList.toggle("intro--white"), subtitle.classList.toggle("intro__subtitle--white");
}

function timer() {
    setInterval(changeColors, 5e3);
}

document.addEventListener("DOMContentLoaded", timer());

var container = document.querySelector(".container"), prevBtn = document.querySelector(".btn__slider--prev"), nextBtn = document.querySelector(".btn__slider--next"), slides = document.querySelectorAll(".slide-item"), slideIndex = 2;

function moveLeft() {
    showSlides(slideIndex -= 1);
}

function moveRight() {
    showSlides(slideIndex += 1);
}

function showSlides(e) {
    var t = void 0;
    for (e > slides.length && (slideIndex = 2), e < 2 && (slideIndex = slides.length), 
    t = 0; t < slides.length; t++) slides[t].style.display = "none";
    slides[slideIndex - 1].style.display = "block", slides[slideIndex - 2].style.display = "block";
}

prevBtn.addEventListener("click", function() {
    moveLeft();
}), nextBtn.addEventListener("click", function() {
    moveRight();
}), showSlides(slideIndex);